-- Create the books table
CREATE TABLE books (
                       id INT PRIMARY KEY,
                       title VARCHAR(255) NOT NULL,
                       author VARCHAR(255) NOT NULL,
                       genre ENUM('romance', 'horror', 'fantasy', 'mystery', 'sci-fi', 'thriller', 'nonfiction', 'biography') NOT NULL,
                       description TEXT,
                       isbn VARCHAR(20),
                       page_count INT,
                       thumbnail_url VARCHAR(512),
                       publication_date DATE
);


